type AppEnv = {
  REACT_APP_BACKEND_URL: string;
  REACT_APP_FACEBOOK_APP_ID: string;
  REACT_APP_REQUIRE_BUSINESS_MANAGEMENT: boolean;
  REACT_APP_NAME_SYSTEM: string;
