        [ -z "$inpt" ] && [ -n "${subdominio_frontend:-}" ] && inpt="$subdominio_frontend"
        [ -z "$inpt" ] && { echo -e "${RED}$(t err_empty)${NC}"; sleep 1; continue; }
        subdominio_frontend="$inpt"; echo -e "${GRAY_LIGHT}$(t checking_dns)${NC}"
