        case "${inpt^^}" in B) [ $step -gt 1 ] && step=$((step-1)); continue;; R) unset subdominio_backend; step=1; continue;; Q) echo -e "${YELLOW}$(t cancelled)${NC}"; exit 1;; esac
        [ -z "$inpt" ] && [ -n "${subdominio_backend:-}" ] && inpt="$subdominio_backend"
