        case "${inpt^^}" in B) step=$((step-1)); continue;; R) unset email_deploy; step=1; continue;; Q) echo -e "${YELLOW}$(t cancelled)${NC}"; exit 1;; esac
        [ -z "$inpt" ] && [ -n "${email_deploy:-}" ] && inpt="$email_deploy"
