  printf "${CYAN_LIGHT}│${NC}  ${GREEN}█   █▀█ █▀█  █  █ █▀█${NC}  ${CYAN_LIGHT}│${NC}  ${GRAY_LIGHT}$(t banner_sub)${NC}\n"
  printf "${CYAN_LIGHT}│${NC}  ${GREEN}▀▀▀ ▀ ▀ ▀ ▀  ▀  ▀ ▀ ▀${NC}  ${CYAN_LIGHT}│${NC}  ${GRAY_LIGHT}$(t banner_tag)${NC}\n"
