        numero_suporte="$inpt"; step=$((step+1))
      ;;
      8) header_step "$step" "$total" "$(t q_fb_id)"; [ -n "${facebook_app_id:-}" ] && echo -e "${GRAY_LIGHT}(${facebook_app_id})${NC}"; read -rp "> " inpt || true
