    echo -e "${GRAY_LIGHT}$(t lang_options)${NC}"
    echo
    read -rp "> " opt
    case "$opt" in
      1) LANG_UI="pt"; break;;
      2) LANG_UI="es"; break;;
      3) LANG_UI="en"; break;;
      *) echo -e "${YELLOW}$(t invalid_option)${NC}"; sleep 1;;
    esac
  done
  case "$LANG_UI" in
    pt) YES_CHARS="S|Y|SI"; NO_CHARS="N|NO";;
    es) YES_CHARS="S|SI|Y"; NO_CHARS="N|NO";;
    en) YES_CHARS="Y|S|SI"; NO_CHARS="N|NO";;
