coletar_dados_usuario() {
  local total=12
  ip_atual="$(get_vps_ip)"; if [ -z "$ip_atual" ]; then echo -e "${RED}$(t err_dns_ip)${NC}"; exit 1; fi
  local step=1 inpt
  while true; do
    case "$step" in
      1)
