        banner_title) echo "Full Installer • ChatIA";;
        banner_sub) echo "Ultra Optimized v5.0";;
        banner_tag) echo "Powered by ChatIA Technology";;

        wizard_hint) echo "Tips: [B] Back  [R] Restart  [Q] Quit  [Enter] keeps previous value";;
