        read -rp "$(t q_confirm) " confirmacao
        if yes_input "$confirmacao"; then max_whatsapps=9999; max_users=9999; salvar_variaveis; break; else echo -e "${YELLOW}$(t cancelled)${NC}"; exit 1; fi
      ;;
    esac
  done
}

