#============================= LOG / PROGRESS ==================================

log_info(){ echo -e "${BLUE}[INFO]${NC} $1"; }
log_success(){ echo -e "${GREEN}[OK]${NC} $1"; }
log_warning(){ echo -e "${YELLOW}[WARN]${NC} $1"; }
log_error(){ echo -e "${RED}[ERROR]${NC} $1"; }

SPINNER_FRAMES=( "â ‹" "â ™" "â ¹" "â ¸" "â ¼" "â ´" "â ¦" "â §" "â ‡" "â " )
spinner() {
  local i=0
  while kill -0 "$1" 2>/dev/null; do
    printf "\r${CYAN_LIGHT}%s${NC} " "${SPINNER_FRAMES[$i]}"
    i=$(( (i+1) % ${#SPINNER_FRAMES[@]} ))
    sleep 0.12
  done
}

PROG_TOTAL=32
PROG_DONE=0

progress_render() {
  local width=40
  local percent=0
  if [ "$PROG_TOTAL" -gt 0 ]; then
    percent=$(( PROG_DONE * 100 / PROG_TOTAL ))
    [ "$percent" -gt 100 ] && percent=100
  fi
  local filled=$(( percent * width / 100 ))
  local empty=$(( width - filled ))
  printf "\r${WHITE}["
  if [ "$filled" -gt 0 ]; then printf "%0.sâ–ˆ" $(seq 1 "$filled"); fi
  if [ "$empty" -gt 0 ]; then printf "%0.sâ–‘" $(seq 1 "$empty"); fi
  printf "] %3d%%${NC}" "$percent"
}

progress_inc() {
  PROG_DONE=$(( PROG_DONE + 1 ))
  progress_render
  printf "\n"
}

run_quiet() {
  local desc="$1"; shift
  printf "\r\033[K${GRAY_LIGHT}â€¢ ${desc}${NC}\n" | tee -a "$LOG_FILE"
  ( eval "$*" ) >> "$LOG_FILE" 2>&1 &
  local pid=$!
  spinner "$pid"
  wait "$pid"
  local ec=$?
  if [ $ec -ne 0 ]; then
    echo
    log_error "$(t see_logs) $LOG_FILE"
    tail -n 120 "$LOG_FILE" || true
    exit $ec
  fi
  log_success "$(t done_ok)"
  progress_inc
}

run_quiet_may_fail() {
  local desc="$1"; shift
  printf "\r\033[K${GRAY_LIGHT}â€¢ ${desc}${NC}\n" | tee -a "$LOG_FILE"
  ( eval "$*" ) >> "$LOG_FILE" 2>&1 &
  local pid=$!
  spinner "$pid"
  wait "$pid" || true
  local ec=$?
  if [ $ec -ne 0 ]; then
    log_warning "$(t ssl_warn)"
  else
    log_success "$(t done_ok)"
  fi
  progress_inc
}

