+    printf \"[INFO] Atualizada diretiva client_max_body_size em conf.d.\\n\"
+  else
+    printf \"[INFO] Diretiva client_max_body_size já configurada; mantendo arquivo atual.\\n\"
+  fi
+
+  cat <<'NGINX_BACKEND' > \"\$TMP_BACKEND\"
