+        proxy_set_header X-Forwarded-Proto \$scheme;
+        proxy_cache_bypass \$http_upgrade;
+    }
+}
+NGINX_FRONTEND
+  sed -i \"s/FRONTEND_HOST_PLACEHOLDER/${frontend_host}/g; s/FRONTEND_PORT_PLACEHOLDER/${frontend_port}/g\" \"\$TMP_FRONTEND\"
